--
-- 表的结构 `bbs_user`
--

CREATE TABLE IF NOT EXISTS `bbs_user` (
  `bbs_id` mediumint(8) NOT NULL AUTO_INCREMENT COMMENT '//用户ID',
  `bbs_username` varchar(20) NOT NULL COMMENT '//用户名',
  `bbs_password` varchar(20) NOT NULL COMMENT '//密码',
  `bbs_sex` char(2) NOT NULL DEFAULT '男' COMMENT '//性别',
  `bbs_face` varchar(30) NOT NULL DEFAULT 'image/faces/0.jpg' COMMENT '//头像',
  `bbs_signature` varchar(40) NOT NULL COMMENT '//个性签名',
  `bbs_identity` tinyint(1) NOT NULL DEFAULT '0' COMMENT '//身份，0是一般用户，1是管理员',
  `bbs_register_time` datetime NOT NULL DEFAULT '0000-00-00 00:00:00' COMMENT '//注册时间',
  PRIMARY KEY (`bbs_id`)
) ENGINE=InnoDB  DEFAULT CHARSET=utf8 AUTO_INCREMENT=35 ;

